import React from 'react'
// import Table from '@mui/material/Table';
// import TableBody from '@mui/material/TableBody';
// import TableCell from '@mui/material/TableCell';
// import TableContainer from '@mui/material/TableContainer';
// import TableHead from '@mui/material/TableHead';
// import TableRow from '@mui/material/TableRow';
import {
    Table, 
    TableBody, 
    TableCell, 
    TableContainer, 
    TableHead, 
    TableRow
} from '@mui/material'

export const CommonTable = ({data}) => {
    // get all unique grades present in the data
    const uniqueGrades = [...new Set(data.map(obj => obj.grade))]
    // sort the grades from lowest to highest
    const sortedGrades = Array.from(uniqueGrades).sort()

    const generateColumns = () => {
        return sortedGrades.map(grade => 
            <TableCell key={grade}>{`Grade ${grade}`}</TableCell>)
    }

    const generateRows = () => {
        const currentBalanceSum = {}

        sortedGrades.map(grade => currentBalanceSum[grade] = 0)
        console.log('currentBalanceSum', currentBalanceSum)

        // data.map(obj => 
        //     if ()
        // )


    }

    return (
        <TableContainer>
            <Table>
                <TableHead>
                    <TableRow>
                        {generateColumns()}
                    </TableRow>
                </TableHead>
                <TableBody>
                    {generateRows()}
                </TableBody>
            </Table>
        </TableContainer>
    )
}
